import { Navigate } from "react-router-dom";

export const ProtectedRoute = ({ children }) => {
  const [user] = JSON.parse(localStorage.getItem("loggedInUser"));

  if (!user) {
    return <Navigate to="/auth/login" replace />;
  }
  return children;
};
