import { useNavigate } from "react-router-dom";

export default function Login() {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  function handleSubmit(event) {
    event.preventDefault();
    const users = JSON.parse(localStorage.getItem("users"));
    const user = users.find((user) => user.email === email);
    if (user && user.password === password) {
      navigate("/");
    } else {
      alert("Invalid email or password");
    }
  }

  function onCreateAccount(event) {
    event.preventDefault();
    navigate("/auth/register");
  }

  return (
    <div className="d-flex justify-content-center align-items-center vh-100">
      <form className="d-flex flex-column col-sm-6 col-lg-3 p-4 border border-1 rounded">
        <h3 className="text-start mb-3">Log in</h3>

        <div className="d-flex flex-column form-group mb-2">
          <label
            className="col-form-label col-form-label-sm text-start"
            htmlFor="email"
          >
            Email Address
          </label>
          <input
            type="text"
            className="form-control form-control-sm"
            id="email"
            name="email"
            placeholder="abc@gmail.com"
          />
        </div>

        <div className="d-flex flex-column form-group mb-4">
          <label
            className="col-form-label col-form-label-sm text-start"
            htmlFor="password"
          >
            Password
          </label>
          <input
            type="password"
            className="form-control form-control-sm"
            id="password"
            name="password"
            placeholder="Enter your password"
          />
        </div>

        <div className="d-grid gap-2">
          <button type="submit" className="btn btn-success btn-sm w-100">
            Login
          </button>
          <p style={{ fontSize: "0.8rem", textAlign: "center" }}>
            Don't have an account?{" "}
            <a href="" onClick={(event) => onCreateAccount(event)}>
              create an account
            </a>
          </p>
        </div>
      </form>
    </div>
  );
}
