import { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function RegistrationForm() {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [fullName, setFullName] = useState("");
  const [companyName, setCompanyName] = useState("");
  const users = JSON.parse(localStorage.getItem("users")) || [];

  function generateUniqId(id = users.length + 1) {
    if (users.some((user) => user.id === id)) {
      return generateUniqId(id + 1);
    } else {
      return id;
    }
  }

  function handleSubmit(event) {
    event.preventDefault();
    users.push({
      id: generateUniqId(),
      email,
      password,
      companyName,
      fullName,
      roll: "seller",
      isEmailVerified: false,
    });
    localStorage.setItem("users", JSON.stringify(users));
    setEmail("");
    setPassword("");
    setFullName("");
    setCompanyName("");
    navigate("/auth/login");
  }

  function onLoginToAccount(event) {
    event.preventDefault();
    navigate("/auth/login");
  }

  return (
    <div className="d-flex justify-content-center align-items-center vh-100">
      <form
        className="d-flex flex-column col-sm-6 col-lg-3 p-4 border border-1 rounded"
        onSubmit={(event) => handleSubmit(event)}
      >
        <h3 className="text-start mb-3">Register User</h3>

        <div className="d-flex flex-column form-group mb-2">
          <label
            className="col-form-label col-form-label-sm text-start"
            htmlFor="email"
          >
            Email Address
          </label>
          <input
            type="text"
            className="form-control form-control-sm"
            id="email"
            name="email"
            placeholder="abc@gmail.com"
            onChange={(e) => setEmail(e.target.value)}
            value={email}
          />
        </div>

        <div className="d-flex flex-column form-group mb-2">
          <label
            className="col-form-label col-form-label-sm text-start"
            htmlFor="password"
          >
            Password
          </label>
          <input
            type="password"
            className="form-control form-control-sm"
            id="password"
            name="password"
            placeholder="Enter your password"
            onChange={(e) => setPassword(e.target.value)}
            value={password}
          />
        </div>

        <div className="d-flex flex-column form-group mb-2">
          <label
            className="col-form-label col-form-label-sm text-start"
            htmlFor="fullName"
          >
            Full Name
          </label>
          <input
            type="text"
            className="form-control form-control-sm"
            id="fullName"
            name="fullName"
            params
            placeholder="Enter your full name"
            onChange={(e) => setFullName(e.target.value)}
            value={fullName}
          />
        </div>

        <div className="d-flex flex-column form-group mb-3">
          <label
            className="col-form-label col-form-label-sm text-start"
            htmlFor="companyName"
          >
            Company Name
          </label>
          <input
            type="text"
            className="form-control form-control-sm"
            id="companyName"
            name="companyName"
            placeholder="Enter your company name"
            onChange={(e) => setCompanyName(e.target.value)}
            value={companyName}
          />
        </div>

        <div
          className="d-grid gap-2"
          style={{ fontSize: "0.8rem", textAlign: "center" }}
        >
          <button type="submit" className="btn btn-success btn-sm">
            Sign up
          </button>
          <>
            <p>Already have an account? </p>
            <a href="#" onClick={(event) => onLoginToAccount(event)}>
              login to account
            </a>
          </>
        </div>
      </form>
    </div>
  );
}
