import { useState } from "react";

export default function RegistrationForm() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [fullName, setFullName] = useState("");
  const [companyName, setCompanyName] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();
    let users = JSON.parse(localStorage.getItem("users"));
    users.push({ email, password, companyName, fullName });
    console.log(users);
    localStorage.setItem("users", JSON.stringify(users));
    console.log("usersssss", JSON.parse(localStorage.getItem("users")));
    setEmail("")
    setPassword("")
    setFullName
  };

  const handleReset = (event) => {
    event.preventDefault();
    console.log("Form reset");
  };

  return (
    <div className="d-flex justify-content-center align-items-center vh-100">
      <form
        className="d-flex flex-column col-sm-8 col-lg-4 p-4 border border-1 rounded"
        onSubmit={handleSubmit}
      >
        <h3 className="text-start mb-4">Register User</h3>

        <div className="d-flex flex-column form-group mb-2">
          <label
            className="col-form-label col-form-label-sm text-start"
            htmlFor="email"
          >
            Email Address
          </label>
          <input
            type="text"
            className="form-control form-control-sm"
            id="email"
            name="email"
            placeholder="abc@gmail.com"
            onChange={(e) => setEmail(e.target.value)}
            value={email}
          />
        </div>

        <div className="d-flex flex-column form-group mb-2">
          <label
            className="col-form-label col-form-label-sm text-start"
            htmlFor="password"
          >
            Password
          </label>
          <input
            type="password"
            className="form-control form-control-sm"
            id="password"
            name="password"
            placeholder="Enter your password"
            onChange={(e) => setPassword(e.target.value)}
            value={password}
          />
        </div>

        <div className="d-flex flex-column form-group mb-2">
          <label
            className="col-form-label col-form-label-sm text-start"
            htmlFor="fullName"
          >
            Full Name
          </label>
          <input
            type="text"
            className="form-control form-control-sm"
            id="fullName"
            name="fullName"
            placeholder="Enter your full name"
            onChange={(e) => setFullName(e.target.value)}
            value={fullName}
          />
        </div>

        <div className="d-flex flex-column form-group mb-3">
          <label
            className="col-form-label col-form-label-sm text-start"
            htmlFor="companyName"
          >
            Company Name
          </label>
          <input
            type="text"
            className="form-control form-control-sm"
            id="companyName"
            name="companyName"
            placeholder="Enter your company name"
            onChange={(e) => setCompanyName(e.target.value)}
            value={companyName}
          />
        </div>

        <div className="d-flex gap-2">
          <button type="submit" className="btn btn-success btn-sm">
            Sign up
          </button>
          <button
            type="button"
            className="btn btn-outline-danger btn-sm"
            onClick={handleReset}
          >
            Reset
          </button>
        </div>
      </form>
    </div>
  );
}
